
#include<iostream>
#include<string>

using namespace std;

string addStrings(string num1, string num2) {
        int endA = num1.length() -1;
        int endB = num2.length() -1;
        string result = "";
        int pre = 0;

        while(endA >= 0 && endB >= 0) {
            int numA = num1[endA--] - '0';
            int numB = num2[endB--] - '0';
	    result += (numA + numB + pre) % 10  + '0';
            pre = (numA + numB) / 10;
            
        }
        
        while(endA >= 0) {
            int numA = num1[endA--] - '0';
            result += (numA + pre) % 10 + '0';
            pre = (numA + pre) / 10;
        }
        
        while(endB >= 0) {
            int numB = num2[endB--] - '0';
            result += (numB + pre) % 10 + '0';
            pre = (numB + pre) / 10;
        }
        
        if(pre == 1) {
            result += "1";
        }
        
        int mid = result.length() / 2;
        int l = result.length();
        for(int i = 0 ; i < mid; i++){
            char t = result[i];
            result[i] = result[l - i - 1];
            result[l - i - 1] = t;
        }
        return result;
    }


int main() {
	string a = "1";
	string b = "";
	
	std::cout << addStrings(a, b) << std::endl;
}
